import React, { useEffect, useState } from 'react';
import axios from 'axios';

import backgroundImage from './Sky.jpg';
import './style.css';
import FunFactsForm from './FunFactsForm';
import NotesManager from './NotesManager';
import './NotesManager.css';

function App() {
  const [birdItems, setBirdItems] = useState([]);
  const [search, setSearch] = useState('');
  const [birdImages, setBirdImages] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [showWithDescription, setShowWithDescription] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [grayscaleLevel, setGrayscaleLevel] = useState(0);
  const backgroundStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    filter: `grayscale(${grayscaleLevel}%)`, // Grayscale filter
  };

  const fetchBirdItems = async () => {
    try {
      const response = await axios.get('/api/items'); // Fetch data from Express server
      setBirdItems(response.data);
    } catch (error) {
      console.error('Error fetching bird items:', error);
    }
  };

  useEffect(() => {
    fetchBirdItems();
  }, []); // Empty dependency array for initial fetch only

  const handleRefresh = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get('https://api.unsplash.com/photos/random', {
        params: {
          query: 'bird',
          count: 20, // Number of random images to fetch
          client_id: '4GniCvmTYibDrrna0zYzmFULv4UXv2GL5vMAqylCbPE', // Unsplash access key
        },
      });
      const randomBirdImages = response.data;
      setBirdImages(randomBirdImages);
    } catch (error) {
      console.error('Error fetching random bird images:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app" style={backgroundStyle}>
      <div className="header">
        <h1>
          <a href="/"> {isLoading ? 'Loading Random Bird Images' : 'Bird Sanctuary'} </a>
        </h1>
        <input
          type="search"
          placeholder="Search for birds"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button onClick={handleRefresh}>Refresh</button>
        <input
          type="range"
          min="0"
          max="100"
          value={grayscaleLevel}
          onChange={(e) => setGrayscaleLevel(Number(e.target.value))}
        />
      </div>

      <div className="container">
        <div className="anime-row">
          <h2 className="text-heading">Bird Images</h2>
          <div className="row">
            {/* Display bird images */}
            {birdImages.map((birdImage) => (
              <div key={birdImage.id} className="bird-image">
                <img className="bird-img" src={birdImage.urls.small} alt="Bird" />
                {showWithDescription && (
                  <div className="image-description">{birdImage.alt_description || 'Description not available'}</div>
                )}
                <button className="favorite-btn">
                  {favorites.some((fav) => fav.id === birdImage.id) ? 'Remove from Favorites' : 'Add to Favorites'}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="favorites-container">
        <h2 className="text-heading">Favorites</h2>
        <div className="favorites-row">
          {/* Display favorites */}
          {favorites.map((favorite) => (
            <div key={favorite.id} className="favorite-image">
              <img className="favorite-img" src={favorite.urls.small} alt="Bird" />
              <p>{favorite.alt_description || 'Description not available'}</p>
              <button className="remove-favorite-btn">Remove from Favorites</button>
            </div>
          ))}
        </div>
      </div>

      {/* Add the NotesManager component */}
      <NotesManager />

      {/* FunFactsForm component remains unchanged */}
      <FunFactsForm />

      <div className="bird-items">
        <h2>Bird Items</h2>
        <ul>
          {birdItems.map((item, index) => (
            <li key={index}>
              <strong>{item.name}</strong>: {item.description}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
