import React, { useState } from 'react';
import './NotesManager.css'; // Import CSS for styling

const NotesManager = () => {
  const [notes, setNotes] = useState(['']); // Initial state with one empty note
  const [newNote, setNewNote] = useState(''); // State for the new note input
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State for success message

  const handleAddNote = () => {
    setNotes([...notes, '']); // Add a new empty note to the notes array
  };

  const handleRemoveNote = (index) => {
    const updatedNotes = [...notes];
    updatedNotes.splice(index, 1);
    setNotes(updatedNotes);
  };

  const handleNoteChange = (index, value) => {
    const updatedNotes = [...notes];
    updatedNotes[index] = value;
    setNotes(updatedNotes);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    // Here you can perform any additional actions you need before showing the success message
    setShowSuccessMessage(true); // Show the success message
  };

  return (
    <div className="notes-manager-container">
      <h2>My Notes</h2>
      <form onSubmit={handleSubmit}>
        <div className="notes-list">
          {notes.map((note, index) => (
            <div key={index} className="note-item">
              <input
                type="text"
                value={note}
                onChange={(e) => handleNoteChange(index, e.target.value)}
                placeholder="Enter a note"
              />
              <button onClick={() => handleRemoveNote(index)}>Remove</button>
            </div>
          ))}
        </div>
        <div className="add-note-form">
          <button type="button" onClick={handleAddNote}>Add Note</button> {/* Changed type to "button" */}
          <button type="submit">Submit Notes</button>
        </div>
      </form>
      {showSuccessMessage && (
        <div className="success-message">
          Notes Form Successfully Submitted!
        </div>
      )}
    </div>
  );
};

export default NotesManager;
