import React, { useEffect, useState } from 'react';
import axios from 'axios';
import backgroundImage from './Sky.jpg';
import './style.css';
import FunFactsForm from './FunFactsForm';
import NotesManager from './NotesManager';
import './NotesManager.css';

function App() {
  const [search, setSearch] = useState('');
  const [birdImages, setBirdImages] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [showWithDescription, setShowWithDescription] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [grayscaleLevel, setGrayscaleLevel] = useState(0);
  const backgroundStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    filter: `grayscale(${grayscaleLevel}%)`, // Grayscale filter
  };

  const fetchBirdImages = async () => {
    setIsLoading(true);
    try {
      let query = 'bird'; // Default query includes 'bird'
      if (search.trim()) {
        query += ` ${search.trim()}`; // Append user's search input to the query
      }
      const response = await axios.get('https://api.unsplash.com/search/photos', {
        params: {
          query: query,
          per_page: 20,
          client_id: '4GniCvmTYibDrrna0zYzmFULv4UXv2GL5vMAqylCbPE', // Replace with your Unsplash access key
        },
      });
      const birdImageData = response.data.results;
      setBirdImages(birdImageData);
    } catch (error) {
      console.error('Error fetching bird images:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchBirdImages();
  }, [search]); // Trigger fetchBirdImages when search query changes

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(storedFavorites);
  }, []);

  const handleFavorite = (photo) => {
    const existingIndex = favorites.findIndex((fav) => fav.id === photo.id);
    if (existingIndex === -1) {
      const updatedFavorites = [...favorites, photo];
      setFavorites(updatedFavorites);
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    } else {
      const updatedFavorites = favorites.filter((fav) => fav.id !== photo.id);
      setFavorites(updatedFavorites);
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    }
  };

  const handleFilterWithDescription = () => {
    setShowWithDescription(!showWithDescription);
  };

  const handleRefresh = async () => {
    setSearch(''); // Clear the search query
    setIsLoading(true);
    try {
      const response = await axios.get('https://api.unsplash.com/photos/random', {
        params: {
          query: 'bird',
          count: 20, // Number of random images to fetch
          client_id: 'YOUR_UNSPLASH_CLIENT_ID_HERE', // Replace with your Unsplash access key
        },
      });
      const randomBirdImages = response.data;
      setBirdImages(randomBirdImages);
    } catch (error) {
      console.error('Error fetching random bird images:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGrayscaleChange = (e) => {
    setGrayscaleLevel(Number(e.target.value));
  };

  const handleFunFactsSubmit = (answer) => {
    console.log('User submitted answer:', answer);
  };

  const [audioFiles] = useState([
    { name: "Blue Jay", path: "/bluejay.mp3" },
    { name: "Jungle", path: "/jungle.mp3" },
    { name: "Mourning Dove", path: "/mourningdove.mp3" },
    { name: "Nature", path: "/nature.mp3" },
    { name: "Rainforest", path: "/rainforest.mp3" },
    { name: "Raven", path: "/raven.mp3" },
  ]);

  const [selectedFile, setSelectedFile] = useState(null);
  const [audioSrc, setAudioSrc] = useState(null);
  const [audioDuration, setAudioDuration] = useState(0); // State to store audio duration

  const handleFileSelect = (fileName) => {
    setSelectedFile(fileName);
    setAudioSrc(fileName);
  };

  useEffect(() => {
    if (audioSrc) {
      const audio = new Audio(audioSrc);
      audio.addEventListener("loadedmetadata", () => {
        setAudioDuration(audio.duration);
      });
    }
  }, [audioSrc]);

  return (
    <div className="app" style={backgroundStyle}>
      <div className="header">
        {/* Header content here */}
      </div>

      <div className="container">
        {/* Bird Images and Favorites section */}
      </div>

      <NotesManager />
      <FunFactsForm onSubmit={handleFunFactsSubmit} />

      <div className="bird-items">
        {/* Most Common Birds list */}
      </div>

      <div className="bird-sounds-container">
        <h2>Bird Sounds Library</h2>
        <ul>
          {audioFiles.map((sound, index) => (
            <li key={index}>
              <strong>{sound.name}</strong>
              <audio controls>
                <source src={`http://localhost:8080${sound.path}`} type="audio/mpeg" />
                Your browser does not support the audio element.
              </audio>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
